import math

def checkPQN(p, q, n):
    if p*q == n:
        return "Correct p,q,n relation"
    return "Wrong p,q,n relation"

def checkPQDE(p,q,d,e):
    toReturn = ""
    lamb_n = math.lcm(p-1,q-1)
    
    if e < 1 or e > lamb_n or math.gcd(e,lamb_n) != 1:
        toReturn += "wrong e and lamb_n relation"
    if e*d % lamb_n != 1:
        toReturn += "\nWrong d"
    if toReturn == "":
        return "Correct p,q,d,e, relation"
    return toReturn

